DROP VIEW IF EXISTS flat_tweets;

CREATE VIEW flat_tweets AS
SELECT
  t.id, t.id_str, t.created_at, t.text, t.source, t.truncated, t.in_reply_to_status_id, t.in_reply_to_status_id_str, t.in_reply_to_user_id, t.in_reply_to_user_id_str, t.in_reply_to_screen_name, t.retweet_count, t.favorite_count, t.favorited, t.retweeted, t.possibly_sensitive, t.filter_level, t.lang, t.timestamp_ms, t.retweeted_status_id, t.retweeted_status_id_str, t.dead_time, t.dead_time_total_retweet, t.geo_lon, t.geo_lat, t.has_hashtags, t.has_trends, t.has_urls, t.has_user_mentions, t.has_symbols, t.has_photo, u.id AS user_id, u.id_str AS user_id_str, u.name AS user_name, u.screen_name AS user_screen_name, u.location AS user_location, u.url AS user_url, u.description AS user_description, u.protected AS user_protected, u.verified AS user_verified, u.followers_count AS user_followers_count, u.friends_count AS user_friends_count, u.listed_count AS user_listed_count, u.statuses_count AS user_statuses_count, u.created_at AS user_created_at, u.utc_offset AS user_utc_offset, u.time_zone AS user_time_zone, u.lang AS user_lang, u.contributors_enabled AS user_contributors_enabled, u.is_translator AS user_is_translator, u.profile_background_color AS user_profile_background_color, u.profile_background_image_url AS user_profile_background_image_url, u.profile_background_image_url_https AS user_profile_background_image_url_https, u.profile_background_tile AS user_profile_background_tile, u.profile_link_color AS user_profile_link_color, u.profile_sidebar_border_color AS user_profile_sidebar_border_color, u.profile_sidebar_fill_color AS user_profile_sidebar_fill_color, u.profile_text_color AS user_profile_text_color, u.profile_use_background_image AS user_profile_use_background_image, u.profile_image_url AS user_profile_image_url, u.profile_image_url_https AS user_profile_image_url_https, u.profile_banner_url AS user_profile_banner_url, u.default_profile AS user_default_profile, u.default_profile_image AS user_default_profile_image
FROM original_tweets t, users u
WHERE t.user_id = u.id;
